import { Component } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { ContComponent } from './medicina/cont/cont.component';
import { GlosarioComponent } from './medicina/glosario/glosario.component';
import { MainPageComponent } from './medicina/home/main-page.component';
@Component({
  selector: 'app-medicina-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})

export class HeaderComponent {
  constructor(private router: Router) {

  }
 ngOnInit() {

   // Suscribirse a los eventos de navegación del enrutador
   this.router.events.subscribe((event) => {
     if (event instanceof NavigationEnd) {
       this.iscontPage = event.url.includes('/contenido'); // Comprobar si la página actual es la página de la contenido
       this.isglosarioPage = event.url.includes('/glosario'); // Comprobar si la página actual es la página de glosario
       this.actualizarVisibilidadBotones(); // Actualizar la visibilidad de los botones según la página actual
     }
   });

 }
}
