import { Component } from '@angular/core';
import { NavigationEnd, Router } from '@angular/router';

@Component({
  selector: 'app-medicina-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  iscontPage = false;
  isglosarioPage = false;

  constructor(private router: Router) {}

  ngOnInit() {
    // Suscribirse a los eventos de navegación del enrutador
    this.router.events.subscribe((event) => {
      if (event instanceof NavigationEnd) {
        this.iscontPage = event.url.includes('/contenido'); // Comprobar si la página actual es la página de contenido
        this.isglosarioPage = event.url.includes('/glosario'); // Comprobar si la página actual es la página de glosario
      }
    });
  }
}
<div class="container">

  <header
    class="d-flex flex-wrap align-items-center justify-content-center justify-content-md-between py-3 mb-4 border-bottom">
    <div class="col-md-3 mb-2 mb-md-0">
      <a href="/" class="d-inline-flex link-body-emphasis text-decoration-none">
        <img src="../../../../assets/logo.png" alt="Imagen" class="custom-image">

      </a>
    </div>
    <ul class="nav col-12 col-md-auto mb-2 justify-content-center mb-md-0">
      <li><a  routerLink="" class="nav-link px-2 link-secondary">INICIO</a></li>
      <li><a routerLink= "/contenido1" class="nav-link px-2">CONTENIDO</a></li>
      <li><a routerLink="/glosario" class="nav-link px-2">GLOSARIO</a></li>
    </ul>

    <div class="col-md-3 text-end">
      <button type="button" class="btn btn-outline-primary me-2">Cuenta</button>
    </div>
  </header>
</div>
