import { Component } from '@angular/core';

interface Glosario {
  [key: string]: string;
}

@Component({
  selector: 'app-glosario',
  templateUrl: './glosario.component.html',
  styleUrls: ['./glosario.component.css']
})
export class GlosarioComponent {
  glosario:Glosario = {
    'A': 'A',
    'B': 'B',
    'C': 'C',
    // Más contenido aquí
  };
  seleccionActual = 'A'; // Comienza mostrando el contenido para 'A'
searchText: any;
  onSearchChange() {
    console.log(this.searchText);
    // Aquí puedes manejar la entrada del usuario
    // Por ejemplo, puedes realizar una solicitud HTTP para buscar los datos
  }

}
<!-- HEADER -->
<app-medicina-header></app-medicina-header>

<div class="linea text-center" id="cont">
  <h1 class="texto">GLOSARIO</h1>
</div>

<div class="d-flex justify-content-end">
  <form class="form-inline my-2 my-lg-0">
    <input class="form-control mr-sm-2" [(ngModel)]="searchText" (ngModelChange)="onSearchChange()" type="search"
      placeholder="Buscar" aria-label="Buscar">
  </form>
</div>
<div class="d-flex justify-content-center align-items-center carousel">
  <carousel [isAnimated]="false">
    <slide *ngFor="let letra of glosario | keyvalue">
      <div class="carousel-caption">
        <ul class="letras-lista">
          <p [class.active]="letra.key === seleccionActual" (click)="seleccionActual = letra.key">
          </p>
        </ul>
      </div>
      <div class="carousel-body" id="letra">
        {{ letra.value }}
      </div>
    </slide>
    <ng-template carouselControl>
      <button type="button" class="carousel-control-prev" aria-label="Previous">
        <span class="carousel-control-prev-icon" aria-hidden="true"></span>
      </button>
      <button type="button" class="carousel-control-next" aria-label="Next">
        <span class="carousel-control-next-icon" aria-hidden="true"></span>
      </button>
    </ng-template>
  </carousel>

</div>

<div class="informacion-letra">
  <h2>Información sobre la letra {{ seleccionActual }}</h2>
  <p>{{ glosario[seleccionActual] }}</p>
</div>





<!-- FOOTER -->
<app-medicina-footer></app-medicina-footer>
