import { Component } from '@angular/core';

@Component({
  selector: 'app-glosario',
  templateUrl: './glosario.component.html',
  styleUrls: ['./glosario.component.css']
})
export class GlosarioComponent {
  glosario = {
    'A': 'Contenido para la letra A',
    'B': 'Contenido para la letra B',
    'C': 'Contenido para la letra C',
    // Más contenido aquí
  };
  seleccionActual = 'A'; // Comienza mostrando el contenido para 'A'
searchText: any;
  onSearchChange() {
    console.log(this.searchText);
    // Aquí puedes manejar la entrada del usuario
    // Por ejemplo, puedes realizar una solicitud HTTP para buscar los datos
  }

}
<!-- HEADER -->
<app-medicina-header></app-medicina-header>

<div class="linea text-center" id="cont">
  <h1 class="texto">GLOSARIO</h1>
</div>

<div class="d-flex justify-content-end">
  <form class="form-inline my-2 my-lg-0">
    <input class="form-control mr-sm-2" [(ngModel)]="searchText" (ngModelChange)="onSearchChange()" type="search"
      placeholder="Buscar" aria-label="Buscar">
  </form>
</div>


<carousel>
  <slide *ngFor="let letra of glosario | keyvalue">
    <div class="carousel-caption">
      <h3>{{ letra.key }}</h3>
    </div>
    <img src="..." alt="Imagen {{ letra.key }}">
    <div class="carousel-body">
      {{ letra.value }}
    </div>
  </slide>
</carousel>




<!-- FOOTER -->
<app-medicina-footer></app-medicina-footer>
