import { Component } from '@angular/core';

@Component({
  selector: 'app-pizarra',
  templateUrl: './pizarra.component.html',
  styleUrls: ['./pizarra.component.css']
})
export class PizarraComponent {
  imagenUrl: string | null = null;

  abrirDialogoImagen() {
    const url = window.prompt("Ingresa la URL de la imagen:");
    if (url) {
      this.imagenUrl = url;
    }
  }
}
<app-medicina-header></app-medicina-header>
<div class="pizarra-container">
  <div class="menu-flotante">
    <!-- Opciones del menú flotante -->
    <div class="opcion">
      <button (click)="abrirDialogoImagen()">
        <img src="assets/radix-icons_video.svg" alt="Opción 1">
      </button>

    </div>
    <div class="opcion">
      <img src="assets/Group.svg" alt="Opción 2">
    </div>
    <div class="opcion">
      <img src="assets/Group (1).svg" alt="Opción 2">
    </div>
    <div class="opcion">
      <img src="assets/mdi_drawing.svg" alt="Opción 2">
    </div>
    <div class="opcion">
      <img src="assets/solar_bones-broken.svg" alt="Opción 2">
    </div>
    <!-- Agrega más opciones según sea necesario -->
  </div>
  <div class="pizarra">
    <img *ngIf="imagenUrl" [src]="imagenUrl" alt="Imagen">
  </div>
</div>

<app-medicina-footer></app-medicina-footer>
