import { Component } from '@angular/core';

@Component({
  selector: 'app-cont2',
  templateUrl: './cont2.component.html',
  styleUrls: ['./cont2.component.css']
})
export class Cont2Component {
  constructor(private modalService: NgbModal) {} // Inyecta el servicio

  abrirDialogoImagen() {
    const modalRef = this.modalService.open(ImagenDialogComponent);

    modalRef.componentInstance.imagenUrl = ''; // Inicializa la propiedad imagenUrl del componente hijo

    modalRef.result.then(result => {
      if (result) {
        // Lógica para agregar la imagen a la pizarra
      }
    });
  }
}
