import { Component } from '@angular/core';

@Component({
  selector: 'app-cont2',
  templateUrl: './cont2.component.html',
  styleUrls: ['./cont2.component.css']
})
export class Cont2Component {

  constructor(private dialog: MatDialog, private imagenService: ImagenService) {}

  abrirDialogoImagen() {
    const dialogRef = this.dialog.open(ImagenDialogComponent);

    dialogRef.afterClosed().subscribe(result => {
      if (result) {
        this.imagenService.agregarImagen(result);
      }
    });
  }
}
