import { Component } from '@angular/core';

@Component({
  selector: 'app-cont2',
  templateUrl: './cont2.component.html',
  styleUrls: ['./cont2.component.css']
})
export class Cont2Component {

  agregarOpcion() {

  }
}
<app-medicina-header></app-medicina-header>
<div class="pizarra-container">
  <div class="pizarra">
    <!-- Contenido de la pizarra (videos, fotos, etc.) -->
    <app-video></app-video>
    <app-foto></app-foto>
  </div>
  <div class="opciones">
    <!-- Opciones en el lateral derecho -->
    <button (click)="agregarOpcion()">Agregar Opción</button>
    <!-- Otras opciones -->
  </div>
</div>


<app-medicina-footer></app-medicina-footer>
