  styleUrls: ['./intro.component.css']
})
export class IntroComponent {

}

<div class=" cuad p-4 p-md-5 mb-4 rounded text-body-emphasis bg-body-secondary">
  <div class="col-lg-6 px-0">
    <h1 class="tit display-4 fst-italic">ANATOMÍA HUMANA 3D</h1>
    <p class="lead my-3">Aumenta tu conocimiento sobre la antomía humana, gracias diversos recursos de estudio como
