})
export class IntroComponent {

}

<div class=" cuad p-6 p-md-5 mb-4 rounded text-body-emphasis bg-body-secondary">
  <div class="col-lg-7 px-0">
    <h1 class="tit display-4 fst-italic">ANATOMÍA HUMANA 3D</h1>
